import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  Button,
  Typography,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from '@mui/material';
import QrReader from 'react-qr-scanner';
import { config } from '../config';

function Home() {
  const navigate = useNavigate();
  const [openQrScanner, setOpenQrScanner] = useState(false);
  const [sessionId, setSessionId] = useState('');
  const [error, setError] = useState('');

  // Clear any existing user data when landing on home page
  localStorage.removeItem('userData');

  const createSession = async () => {
    try {
      const response = await fetch(`${config.backendUrl}/api/sessions`, {
        method: 'POST',
      });
      const data = await response.json();
      // Navigate to user setup first, then to session
      navigate('/setup');
    } catch (error) {
      setError('Failed to create session');
    }
  };

  const joinSession = () => {
    if (!sessionId.trim()) {
      setError('Please enter a session ID');
      return;
    }
    // Navigate to user setup with session ID
    navigate(`/setup/${sessionId}`);
  };

  const handleQrScan = (data) => {
    if (data) {
      // Extract session ID from QR code URL
      const url = new URL(data.text);
      const pathSegments = url.pathname.split('/');
      const scannedSessionId = pathSegments[pathSegments.length - 1];

      setOpenQrScanner(false);
      navigate(`/setup/${scannedSessionId}`);
    }
  };

  const handleQrError = (err) => {
    console.error(err);
  };

  return (
    <Container maxWidth="sm">
      <Box
        sx={{
          mt: 8,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          gap: 4,
        }}
      >
        <Typography variant="h3" component="h1" gutterBottom align="center">
          Karaoke Party ðŸŽ¤
        </Typography>

        <Box sx={{ width: '100%', display: 'flex', flexDirection: 'column', gap: 2 }}>
          <Button
            variant="contained"
            size="large"
            onClick={createSession}
            sx={{ py: 2 }}
          >
            Create New Session
          </Button>

          <Typography variant="h6" align="center" sx={{ mt: 4, mb: 2 }}>
            or join existing session
          </Typography>

          <TextField
            fullWidth
            label="Session ID"
            value={sessionId}
            onChange={(e) => setSessionId(e.target.value)}
            error={!!error}
            helperText={error}
          />

          <Button
            variant="outlined"
            size="large"
            onClick={joinSession}
            sx={{ py: 2 }}
          >
            Join Session
          </Button>

          <Button
            variant="text"
            onClick={() => setOpenQrScanner(true)}
            sx={{ mt: 2 }}
          >
            Scan QR Code
          </Button>
        </Box>

        <Dialog open={openQrScanner} onClose={() => setOpenQrScanner(false)}>
          <DialogTitle>Scan Session QR Code</DialogTitle>
          <DialogContent>
            <QrReader
              delay={300}
              onError={handleQrError}
              onScan={handleQrScan}
              style={{ width: '100%' }}
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenQrScanner(false)}>Cancel</Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Container>
  );
}

export default Home;
