import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Box,
  Button,
  Typography,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from '@mui/material';
import QrReader from 'react-qr-scanner';
import { config } from '../config';

function Home() {
  const navigate = useNavigate();
  const [openQrScanner, setOpenQrScanner] = useState(false);
  const [sessionId, setSessionId] = useState('');
  const [error, setError] = useState('');

  const createSession = async () => {
    try {
      const response = await fetch(`${config.backendUrl}/api/sessions`, {
        method: 'POST',
      });
      const data = await response.json();
      navigate(`/setup/${data.sessionId}`);
    } catch (error) {
      setError('Failed to create session');
    }
  };

  const joinSession = () => {
    if (sessionId.length === 4) {
      navigate(`/setup/${sessionId}`);
    } else {
      setError('Please enter a valid 4-digit session ID');
    }
  };

  const handleQrScan = (data) => {
    if (data) {
      const sessionId = data.text.split('/').pop();
      setOpenQrScanner(false);
      navigate(`/setup/${sessionId}`);
    }
  };

  return (
    <Container maxWidth="sm">
      <Box
        sx={{
          mt: 8,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          gap: 4,
        }}
      >
        <Typography variant="h2" component="h1" gutterBottom>
          Karaoke App
        </Typography>

        <Button
          variant="contained"
          color="primary"
          size="large"
          fullWidth
          onClick={createSession}
        >
          Create New Session
        </Button>

        <Typography variant="h6" component="h2">
          - OR -
        </Typography>

        <Box sx={{ width: '100%' }}>
          <TextField
            fullWidth
            label="Enter Session ID"
            value={sessionId}
            onChange={(e) => setSessionId(e.target.value)}
            error={!!error}
            helperText={error}
            sx={{ mb: 2 }}
          />
          <Button
            variant="contained"
            color="secondary"
            size="large"
            fullWidth
            onClick={joinSession}
          >
            Join Session
          </Button>
        </Box>

        <Button
          variant="outlined"
          color="primary"
          size="large"
          fullWidth
          onClick={() => setOpenQrScanner(true)}
        >
          Scan QR Code
        </Button>

        <Dialog open={openQrScanner} onClose={() => setOpenQrScanner(false)}>
          <DialogTitle>Scan Session QR Code</DialogTitle>
          <DialogContent>
            <QrReader
              delay={300}
              onError={(err) => console.error(err)}
              onScan={handleQrScan}
              style={{ width: '100%' }}
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenQrScanner(false)}>Cancel</Button>
          </DialogActions>
        </Dialog>
      </Box>
    </Container>
  );
}

export default Home;
