import React from 'react';
import {
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Paper,
  Typography,
  Box,
} from '@mui/material';
import { PlayArrow, Clear } from '@mui/icons-material';

function SongQueue({ queue, onPlay, onRemove }) {
  if (!queue.length) {
    return (
      <Paper sx={{ p: 2, textAlign: 'center' }}>
        <Typography color="text.secondary">
          No songs in queue
        </Typography>
      </Paper>
    );
  }

  return (
    <Paper sx={{ p: 2 }}>
      <Typography variant="h6" gutterBottom>
        Queue
      </Typography>
      <List>
        {queue.map((song, index) => (
          <ListItem key={`${song.id}-${index}`} divider>
            <ListItemText
              primary={song.title}
              secondary={
                <>
                  <Typography component="span" variant="body2" color="text.primary">
                    {song.artist}
                  </Typography>
                  {' — '}{song.duration}
                </>
              }
            />
            <ListItemSecondaryAction>
              <Box sx={{ display: 'flex', gap: 1 }}>
                <IconButton
                  edge="end"
                  aria-label="play now"
                  onClick={() => onPlay(song, index)}
                >
                  <PlayArrow />
                </IconButton>
                <IconButton
                  edge="end"
                  aria-label="remove from queue"
                  onClick={() => onRemove(index)}
                >
                  <Clear />
                </IconButton>
              </Box>
            </ListItemSecondaryAction>
          </ListItem>
        ))}
      </List>
    </Paper>
  );
}

export default SongQueue;
