import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import {
  Container,
  Box,
  Typography,
  Paper,
  Avatar,
  List,
  ListItem,
  ListItemAvatar,
  ListItemText,
  Grid,
  IconButton,
} from '@mui/material';
import { PlayArrow, Pause } from '@mui/icons-material';
import { io } from 'socket.io-client';
import SongList from '../components/SongList';
import SongQueue from '../components/SongQueue';

function Session() {
  const { sessionId } = useParams();
  const [session, setSession] = useState(null);
  const [users, setUsers] = useState([]);
  const [error, setError] = useState('');
  const [socket, setSocket] = useState(null);
  const [queue, setQueue] = useState([]);
  const [currentSong, setCurrentSong] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    const userData = JSON.parse(localStorage.getItem('userData'));
    if (!userData) {
      setError('User data not found');
      return;
    }

    // Connect to Socket.IO
    const newSocket = io('http://localhost:5000');
    setSocket(newSocket);

    newSocket.emit('joinSession', {
      sessionId,
      user: userData,
    });

    newSocket.on('userJoined', ({ users }) => {
      setUsers(users);
    });

    newSocket.on('syncSession', ({ currentSong, songQueue }) => {
      setCurrentSong(currentSong);
      setQueue(songQueue);
    });

    newSocket.on('queueUpdated', ({ queue }) => {
      setQueue(queue);
    });

    newSocket.on('songStarted', ({ song, startTime }) => {
      setCurrentSong({ ...song, startTime });
      setIsPlaying(true);
    });

    newSocket.on('songPaused', ({ currentTime }) => {
      setIsPlaying(false);
      if (currentSong) {
        setCurrentSong({ ...currentSong, pausedAt: currentTime });
      }
    });

    newSocket.on('songResumed', ({ startTime }) => {
      setIsPlaying(true);
      if (currentSong) {
        setCurrentSong({ ...currentSong, startTime });
      }
    });

    // Fetch session data
    fetch(`http://localhost:5000/api/sessions/${sessionId}`)
      .then((res) => res.json())
      .then((data) => {
        if (data.error) {
          setError(data.error);
        } else {
          setSession(data);
        }
      })
      .catch((err) => {
        setError('Failed to fetch session data');
      });

    return () => {
      newSocket.disconnect();
    };
  }, [sessionId]);

  const handleAddToQueue = (song) => {
    if (socket) {
      socket.emit('addToQueue', { sessionId, song });
    }
  };

  const handlePlay = (song, index) => {
    if (socket) {
      const startTime = Date.now();
      socket.emit('playSong', { sessionId, song, startTime });

      // Remove the song from queue if it was played from queue
      if (typeof index === 'number') {
        const newQueue = [...queue];
        newQueue.splice(index, 1);
        socket.emit('updateQueue', { sessionId, queue: newQueue });
      }
    }
  };

  const handlePause = () => {
    if (socket && currentSong) {
      const currentTime = Date.now() - currentSong.startTime;
      socket.emit('pauseSong', { sessionId, currentTime });
    }
  };

  const handleResume = () => {
    if (socket && currentSong) {
      const currentTime = currentSong.pausedAt || 0;
      socket.emit('resumeSong', { sessionId, currentTime });
    }
  };

  const handleRemoveFromQueue = (index) => {
    if (socket) {
      const newQueue = [...queue];
      newQueue.splice(index, 1);
      socket.emit('updateQueue', { sessionId, queue: newQueue });
    }
  };

  if (error) {
    return (
      <Container>
        <Typography color="error" variant="h6">
          {error}
        </Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg">
      <Box sx={{ mt: 4 }}>
        <Grid container spacing={3}>
          <Grid item xs={12} md={8}>
            {/* Current Song */}
            {currentSong && (
              <Paper sx={{ p: 2, mb: 3 }}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                  <IconButton
                    size="large"
                    onClick={isPlaying ? handlePause : handleResume}
                  >
                    {isPlaying ? <Pause /> : <PlayArrow />}
                  </IconButton>
                  <Box>
                    <Typography variant="h6">{currentSong.title}</Typography>
                    <Typography variant="body2" color="text.secondary">
                      {currentSong.artist}
                    </Typography>
                  </Box>
                </Box>
              </Paper>
            )}

            {/* Song List */}
            <SongList onAddToQueue={handleAddToQueue} />
          </Grid>

          <Grid item xs={12} md={4}>
            {/* Connected Users */}
            <Paper sx={{ p: 2, mb: 3 }}>
              <Typography variant="h6" gutterBottom>
                Connected Users
              </Typography>
              <List>
                {users.map((user, index) => (
                  <ListItem key={index}>
                    <ListItemAvatar>
                      <Avatar src={user.avatar} />
                    </ListItemAvatar>
                    <ListItemText primary={user.name} />
                  </ListItem>
                ))}
              </List>
            </Paper>

            {/* Song Queue */}
            <SongQueue
              queue={queue}
              onPlay={handlePlay}
              onRemove={handleRemoveFromQueue}
            />
          </Grid>
        </Grid>
      </Box>

      {/* Fixed bottom container for QR code and session ID */}
      <Paper
        sx={{
          position: 'fixed',
          bottom: 0,
          left: '50%',
          transform: 'translateX(-50%)',
          p: 2,
          display: 'flex',
          alignItems: 'center',
          gap: 2,
          backgroundColor: 'background.paper',
          boxShadow: 3,
          borderTopLeftRadius: 16,
          borderTopRightRadius: 16,
        }}
      >
        {session?.qrCode && (
          <img
            src={session.qrCode}
            alt="Session QR Code"
            style={{ width: 60, height: 60 }}
          />
        )}
        <Typography variant="body1">
          Session ID: {sessionId}
        </Typography>
      </Paper>
    </Container>
  );
}

export default Session;
