// Get the backend URL based on the current environment
const getBackendUrl = () => {
  if (process.env.NODE_ENV === 'production') {
    return process.env.REACT_APP_BACKEND_URL;
  }

  // For development, try to use the local network IP if accessing from mobile
  const isLocalNetwork = !window.location.hostname.includes('localhost');
  if (isLocalNetwork) {
    // Extract IP from the current URL
    const currentHost = window.location.hostname;
    return `http://${currentHost}:5000`;
  }

  return 'http://localhost:5000';
};

export const config = {
  backendUrl: getBackendUrl(),
};
